package com.example.mudisemployee.fragment;

import static androidx.navigation.Navigation.findNavController;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.appcompat.app.AlertDialog;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;

import android.os.Handler;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.mudisemployee.R;
import com.example.mudisemployee.adapter.OrdersAdapter;
import com.example.mudisemployee.app.App;
import com.example.mudisemployee.databinding.FragmentLoginBinding;
import com.example.mudisemployee.databinding.FragmentMainBinding;
import com.example.mudisemployee.model.OrderModel;
import com.google.android.material.dialog.MaterialAlertDialogBuilder;

import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Date;
import java.util.List;
import java.util.Locale;


public class MainFragment extends Fragment implements OrdersAdapter.OnClickListener {
    private FragmentMainBinding binding;
    private AlertDialog materialDialog;
    private ArrayList<OrderModel> list = new ArrayList<>();
    private OrderModel currentOrder;
    private final Handler handler = new Handler();
    private final SimpleDateFormat timeFormat = new SimpleDateFormat("HH:mm", Locale.getDefault());

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentMainBinding.inflate(inflater);
        materialDialog = createMaterialDialog();

        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        applyClick();
        setAdapter();
        startDataChecking();
    }
    @Override
    public void click(OrderModel order) {
        currentOrder = order;
        Date date = new Date();
        materialDialog.setMessage(requireContext().getString(R.string.order)+
                order.getNumber()+
                requireContext().getString(R.string.finishing_time)+
                date.getHours()+
                ":"+date.getMinutes());
        showMaterialDialog();

    }
    public void showMaterialDialog(){
        materialDialog.show();
    }
    private AlertDialog createMaterialDialog(){
        MaterialAlertDialogBuilder dialog = new MaterialAlertDialogBuilder(requireActivity())
                .setTitle(requireContext().getString(R.string.are_you_sure_that_you_have_finished_the_order))
                .setPositiveButton(requireContext().getString(R.string.yes), (d, which)->{
                    deleteOrder();

                })
                .setNegativeButton(requireContext().getString(R.string.no),(d, which)->{});
        return dialog.create();
    }


    private void applyClick(){

    }
    private void setAdapter(){

        list.add(new OrderModel("https://www.themediterraneandish.com/wp-content/uploads/2022/08/ground-beef-pita-sandwich-recipe-5.jpg","10:00","Cash 299",234, Arrays.asList(
                "Pita","Fries","Hot Dog","Pita"
        ),true));

        list.add(new OrderModel("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRAHgyMwVRd174tlvzETIgPoKQ3mT8ejvxy6g&s","12:00","Cash 499",254, Arrays.asList(
                "Pita","Fries","Pasta","Pita","Pasta"
        ),true));
        list.add(new OrderModel("data:image/jpeg;base64,","10:00","Card",264, Arrays.asList(
                "Pita","Fries","Hot Dog","Donut","Donut"
        ),false));
        list.add(new OrderModel("data:image/jpeg;base64,","16:00","Cash 99",274, Arrays.asList(
                "Pita","Fries","Hot Dog","Cake","Cake"
        ),false));
        list.add(new OrderModel("data:image/jpeg;base64,","19:00","Card",284, Arrays.asList(
                "Pita","Fries","Hot Dog","Donut","Ice Cream"
        ),false));

        binding.rvOrders.setLayoutManager(new LinearLayoutManager(requireContext()));
        binding.rvOrders.setAdapter(new OrdersAdapter(list,requireContext(),this));
    }
    private void deleteOrder(){
        list.remove(currentOrder);
        App.sharedManager.saveArchive(currentOrder,false);
        binding.rvOrders.getAdapter().notifyDataSetChanged();
    }
    private void startDataChecking(){
        handler.postDelayed(new Runnable() {
            @Override
            public void run() {
                String currentTime = timeFormat.format(new Date());
                binding.currentTime.setText(currentTime);
                handler.postDelayed(this,1000);
            }
        },0);
    }
    private void endDataChecking(){
        handler.removeCallbacksAndMessages(null);
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        endDataChecking();
    }
}
